mapper.select:

select count(*) from board
where main_cat = #{main_cat}
and mini_cat = #{mini_cat}



 	    SELECT *
		FROM (
		        SELECT rownum rn ,  a.*
		        FROM  (select * from board order by b_num desc) a
		        WHERE  main_cat = #{main_cat} AND mini_cat = #{mini_cat} 


		        and p_num=#{p_num}


		     )
		WHERE  rn >= #{start} 
		AND    rn <= #{end}
		order by b_Group desc, b_Step asc, b_num desc

select *
from (select ROWNUM rn, a.*
      from (select *
            from BOARD
            where MINI_CAT = '400'
            and P_NUM = #{p_num}
            order by B_NUM desc) a
      )
where rn between #{start} and #{end}

select count(*) cntAnswer
from product
where p_num = #{p_num, jdbcType=VARCHAR}
and id = #{id, jdbcType=VARCHAR}
