mapper.select:
select count(*)
from member
where id = #{id}

mapper.update:
update member set name=#{name}, phone=#{phone},
				zipCode=#{zipCode}, roadAddress=#{roadAddress}, detailAddress=#{detailAddress}
				where id=#{id}

mapper.select:
		select m.* ,c.c_content current_content 
		from  (
        select rownum rn, a.*
        from (select * from member order by id) a
      ) m ,cat c
		where rn between #{start} and #{end}
		and c.main_cat=m.main_cat
		and c.mini_cat =m.mini_cat

mapper.update:
update member
SET    mini_cat = #{mini_cat}
WHERE  id = #{id}

mapper.select:
select id from member
where name=#{name}
and phone=#{phone}
and m_status=1

select * from member
where id=#{id}
and email=#{email}
and m_status=1

mapper.update:
update member
set password = #{tempPassword}
where id=#{id}
and m_status=1
